        public string GetPostText(string link)
        {
            try
            {
                var post = _receitas.Find(r => r.Link == link)
                        .FirstOrDefault();

                return File.ReadAllText($"{_env.WebRootPath}/Posts/{post.PostId}_post.md");
            }
            catch { return "Não foi possível carregar o detalhe da receita."; }
        }

        public List<BlogPost> GetLatestPosts()
        {
            try
            {
                var options = new FindOptions<BlogPost, BlogPost>
                {
                    Limit = 3,
                    Sort = Builders<BlogPost>.Sort.Descending(o => o.PostId)
                };

                var receitas = _receitas.FindAsync(FilterDefinition<BlogPost>.Empty, options).Result;
                return receitas.ToList();
            }
            catch
            {
                return GetOlderPostsMock();
            }
        }

        public List<BlogPost> GetOlderPosts(int oldestPostId)
        {
            try
            {
                var options = new FindOptions<BlogPost, BlogPost>
                {
                    Limit = 3,
                    Sort = Builders<BlogPost>.Sort.Descending(o => o.PostId)
                };

                var receitas = _receitas.FindAsync(r => r.PostId < oldestPostId, options).Result;
                return receitas.ToList();
            }
            catch
            {
                return GetOlderPostsMock();
            }
        }

        public List<BlogPost> GetOlderPostsMock()
        {
            return Posts.OrderByDescending(x => x.PostId).Take(3).ToList();
        }

        public async Task Create(BlogPost post)
        {
            post.PostId = await GetNewPostId();
            await _receitas.InsertOneAsync(post);
            await RecordToCache(post);
        }

        private async Task RecordToCache(BlogPost post)
        {
            await Task.Run(() =>
             {
                 if (post.PostId <= 100)
                 {
                     try
                     {
                         using (StreamWriter sw = File.CreateText($"{_env.WebRootPath}/Posts/{post.PostId}_post.md"))
                         {
                             sw.WriteLine(post.Title);
                             sw.WriteLine("============");
                             sw.WriteLine(post.ShortDescription);
                             sw.WriteLine("------------");
                             sw.WriteLine("");

                             sw.WriteLine("Ingredientes");
                             sw.WriteLine(post.Ingredients);
                             sw.WriteLine("");

                             sw.WriteLine("Modo de preparo");
                             sw.WriteLine(post.PreparationMode);
                         }
                     }
                     catch { }
                 }
             });           
        }

        private async Task<int> GetNewPostId()
        {
            var options = new FindOptions<BlogPost, BlogPost>
            {
                Limit = 1,
                Sort = Builders<BlogPost>.Sort.Descending(o => o.PostId)
            };

            var oldPost = ( await _receitas.FindAsync(FilterDefinition<BlogPost>.Empty, options)).FirstOrDefault();
            return (oldPost.PostId + 1);
        }

        public void Create(List<BlogPost> posts)
        {
            _receitas.InsertMany(posts);
        }
